record.field:
[options]
# addons_path = /opt/odoo/extra-addons
admin_passwd = {{ odoo_db_manager_password }}
dbfilter = {{ odoo_db_name }}
db_name = {{ odoo_db_name }}
db_user = {{ postgres_user }}
db_password = {{ postgres_password }}
db_host = {{ postgres_container_name }}
db_port = {{ postgres_port }}
list_db = {{ odoo_list_db }}

FROM odoo:{{ odoo_version }}
ARG ODOO_BRANCH={{ odoo_version }} ARG GITHUB_TOKEN=<token> ARG ODOO_ADDONS_TMP_LOCATION=/tmp/odoo-addons ARG ODOO_ADDONS_LOCATION=/opt/odoo/extra-addons
USER root
# Install git RUN DEBIAN_FRONTEND=noninteractive apt update && \ apt-get install -qy --no-install-recommends \ git build-essential \ python3-pip python3-dev \ libffi-dev libssl-dev libsasl2-dev libxslt1-dev libldap2-dev \ libcups2 pkg-config libxmlsec1 libxmlsec1-dev
# Configure git RUN git config --global user.email {{ git_config_user_email }} && \ git config --global user.name {{ git_config_user_name }}
WORKDIR ${ODOO_ADDONS_TMP_LOCATION}
# Install git-aggregator and tools for requirements generation RUN pip3 install --upgrade pip && \ pip install manifestoo setuptools-odoo git-aggregator \ --ignore-installed PyYAML
# Copy git-aggregator.yml COPY ./git-aggregator.yml ${ODOO_ADDONS_TMP_LOCATION}
# Run git-affregator RUN gitaggregate -c git-aggregator.yml --expand-env
# Make default RUN find . -type d -maxdepth 2 -exec setuptools-odoo-make-default \ --addons-dir={} --odoo-version-override=${ODOO_BRANCH} \;
# Build requirements RUN find . -type d -maxdepth 2 -exec setuptools-odoo-get-requirements \ --addons-dir={} --output={}/requirements.txt \;
# Merge all requirements into a single file RUN find ./ -mindepth 0 -name requirements.txt \ -exec cat {} > all_requirements.txt +
# Move all modules to the final destination RUN mkdir -p ${ODOO_ADDONS_LOCATION} && \ find . -mindepth 2 -maxdepth 2 -type d \ -not \( -name '.*' -o -name 'setup' \) \ -exec cp -r {} ${ODOO_ADDONS_LOCATION} \; \ && ls -lah ${ODOO_ADDONS_LOCATION} \ && chown -R odoo ${ODOO_ADDONS_LOCATION}
# Install requirements RUN find ./ -mindepth 0 -name requirements.txt \ -exec cat {} > all_requirements.txt + \ && pip3 install -r all_requirements.txt
# Remove tmp location RUN rm -rf ${ODOO_ADDONS_TMP_LOCATION}
# Let's go! USER odoo
